def switch_names(colnames: list, lookup: dict) -> list:
	"""
	switches the given columns names from format 'int_int' to 'newFormat_newFormat' or 
	"""
	return [lookup[int(j[0])] + "_" + lookup[int(j[1])] for j in [i.split("_") for i in colnames]]


def switch_labs(labels: list, lookup: dict) -> list:
	"""
	switches the fiven names in list from format "int" to format "newFormat"	
	"""
	
	return [lookup[int(j)] for j in labels]
